    directions = {
        'UP': (-1, 0),
        'DOWN': (1, 0),
        'LEFT': (0, -1),
        'RIGHT': (0, 1)
    }

    for command in commands:
        if command == 'UP':
            if direction != 'DOWN':
                direction = 'UP'
        elif command == 'DOWN':
            if direction != 'UP':
                direction = 'DOWN'
        elif command == 'LEFT':
            if direction != 'RIGHT':
                direction = 'LEFT'
        elif command == 'RIGHT':
            if direction != 'LEFT':
                direction = 'RIGHT'

        di, dj = directions[direction]
        ni, nj = i + di, j + dj

        # Check boundaries
        if 0 <= ni < n and 0 <= nj < n:
            i, j = ni, nj

    return (i * n) + j
