def final_position(n, commands):
    # Initial position of the snake
    i, j = 0, 0

    # Directions: UP, RIGHT, DOWN, LEFT
    directions = {
        "UP": (-1, 0),
        "RIGHT": (0, 1),
        "DOWN": (1, 0),
        "LEFT": (0, -1)
    }

    # Start facing RIGHT
    current_direction = "RIGHT"

    for command in commands:
        if command == "TURN":
            # Change direction (for simplicity, assume "TURN" means changing direction 90 degrees clockwise)
            if current_direction == "RIGHT":
                current_direction = "DOWN"
            elif current_direction == "DOWN":
                current_direction = "LEFT"
            elif current_direction == "LEFT":
                current_direction = "UP"
            else:
                current_direction = "RIGHT"
        else:
            # Move in the current direction
            di, dj = directions[current_direction]
            i += di
            j += dj

    # Convert the 2D position back to the linear index
    return i * n + j
