def count_valid_substrings(s, k):
    n = len(s)
    count = 0

    # Check substrings of all lengths
    for length in range(1, n + 1):
        zero_count = one_count = 0

        # Sliding window to count zeros and ones in the current substring
        for i in range(length):
            if s[i] == '0':
                zero_count += 1
            else:
                one_count += 1

        # Check if the current substring satisfies the k-constraint
        if zero_count <= k or one_count <= k:
            count += 1

        # Slide the window across the string
        for start in range(1, n - length + 1):
            end = start + length - 1
            if s[start - 1] == '0':
                zero_count -= 1
            else:
                one_count -= 1

            if s[end] == '0':
                zero_count += 1
            else:
                one_count += 1

            # Check if the new substring satisfies the k-constraint
            if zero_count <= k or one_count <= k:
                count += 1

    return count
