def min_fatigue_level():
    import sys
    input = sys.stdin.read
    data = input().split()

    N = int(data[0])
    presses = [(int(data[i*2+1]), data[i*2+2]) for i in range(N)]

    # Initialize the DP table with infinity
    dp = {}

    def dp_key(left_hand, right_hand):
        return (left_hand, right_hand)

    # Initial state: both hands on key 1
    dp[dp_key(1, 1)] = 0

    for i, (key, hand) in enumerate(presses):
        new_dp = {}
        for (lh, rh), fat in dp.items():
            if hand == 'L':
                new_lh = key
                new_rh = rh
                new_fat = fat + abs(new_lh - lh)
            else:
                new_lh = lh
                new_rh = key
                new_fat = fat + abs(new_rh - rh)

            new_key = dp_key(new_lh, new_rh)
            if new_key not in new_dp or new_fat < new_dp[new_key]:
                new_dp[new_key] = new_fat

        dp = new_dp

    # Find the minimum fatigue level among all final states
    min_fat = min(dp.values())
    print(min_fat)

