    import sys
    input = sys.stdin.read().strip()
    S_AB, S_AC, S_BC = input.split()

    # Dictionary to map '<' and '>' to age comparisons
    comparisons = {
        '<': lambda x, y: x < y,
        '>': lambda x, y: x > y
    }

    # Initialize variables to store the age relationships
    A_B = comparisons[S_AB]
    A_C = comparisons[S_AC]
    B_C = comparisons[S_BC]

    # Determine the order of the brothers
    if A_B(A, B) and A_C(A, C):
        if B_C(B, C):
            return 'B'
        elif A_C(A, C):
            return 'A'
        else:
            return 'C'
    elif A_B(A, B) and B_C(B, C):
        if A_C(A, C):
            return 'A'
        else:
            return 'C'
    elif A_C(A, C) and B_C(B, C):
        if A_B(A, B):
            return 'A'
        else:
            return 'B'

