    input = sys.stdin.read().strip()
    S_AB, S_AC, S_BC = input.split()

    # Initialize the order list with None
    order = [None] * 3

    # Assign indices to brothers
    brother_indices = {'A': 0, 'B': 1, 'C': 2}

    # Determine the order based on the relationships
    if S_AB == '<':
        order[brother_indices['A']] = 0
        order[brother_indices['B']] = 1
    elif S_AB == '>':
        order[brother_indices['A']] = 1
        order[brother_indices['B']] = 0

    if S_AC == '<':
        if order[brother_indices['A']] is not None:
            order[brother_indices['C']] = 2
        else:
            order[brother_indices['A']] = 0
            order[brother_indices['C']] = 1
    elif S_AC == '>':
        if order[brother_indices['A']] is not None:
            order[brother_indices['C']] = 0
        else:
            order[brother_indices['A']] = 1
            order[brother_indices['C']] = 2

    if S_BC == '<':
        if order[brother_indices['B']] is not None:
            order[brother_indices['C']] = 2
        else:
            order[brother_indices['B']] = 1
            order[brother_indices['C']] = 0
    elif S_BC == '>':
        if order[brother_indices['B']] is not None:
            order[brother_indices['C']] = 0
        else:
            order[brother_indices['B']] = 0
            order[brother_indices['C']] = 1

    # Find the middle brother
    middle_index = sorted(order).index(1)
    middle_brother = ['A', 'B', 'C'][middle_index]
