def maxScore(start, d):
    choices = sorted([x + d for x in start] + [x - d for x in start])
    return choices[-1] - choices[0]

def find_power_of_subarrays(nums, k):
    n = len(nums)
    results = []

    for i in range(n - k + 1):
        subarray = nums[i:i+k]
        sorted_subarray = sorted(subarray)

        if subarray == sorted_subarray and all(subarray[j] + 1 == subarray[j+1] for j in range(k-1)):
            results.append(max(subarray))
        else:
            results.append(-1)

    return results

