def count_valid_buildings():
    import sys
    input = sys.stdin.read
    data = input().split()

    N = int(data[0])
    heights = list(map(int, data[1:]))

    result = [0] * N

    for i in range(N):
        for j in range(i + 1, N):
            if heights[j] <= heights[i]:
                result[i] += 1
            else:
                break

