def min_flips(grid):
    m, n = len(grid), len(grid[0])

    def flips_to_palindrome(arr):
        return sum(1 for i in range((len(arr) + 1) // 2) if arr[i] != arr[-i - 1])

    row_flips = sum(flips_to_palindrome(row) for row in grid)
    col_flips = sum(flips_to_palindrome([grid[j][i] for j in range(m)]) for i in range(n))

    return min(row_flips, col_flips)

def min_flips_to_palindrome(grid):
    m, n = len(grid), len(grid[0])

    def flips_for_row(row):
        return sum(1 for i in range((n + 1) // 2) if row[i] != row[n - 1 - i])

    def flips_for_column(col):
        return sum(1 for i in range((m + 1) // 2) if grid[i][col] != grid[m - 1 - i][col])

    total_flips_rows = sum(flips_for_row(row) for row in grid)
    total_flips_columns = sum(flips_for_column(col) for col in range(n))

    return min(total_flips_rows, total_flips_columns)

