def minTime(mountainHeight, workerTimes):
    def canReduce(height, totalTime):
        return sum((totalTime // (wt * (i + 1))) for i, wt in enumerate(workerTimes)) >= height

    left, right = 0, max(workerTimes) * mountainHeight * (mountainHeight + 1) // 2
    while left < right:
        mid = (left + right) // 2
        if canReduce(mountainHeight, mid):
            right = mid
        else:
            left = mid + 1
    return left

def minimumTime(mountainHeight, workerTimes):
    def canReduce(height, time):
        totalWork = 0
        for workerTime in workerTimes:
            x = 1
            while workerTime * x <= time:
                totalWork += workerTime * x
                x += 1
            if totalWork >= height:
                return True
        return False

    left, right = 1, mountainHeight * max(workerTimes)
    while left < right:
        mid = (left + right) // 2
        if canReduce(mountainHeight, mid):
            right = mid
        else:
            left = mid + 1
    return left

