def minValidStrings(words, target):
    n = len(target)
    dp = [float('inf')] * n
    dp[0] = 0

    for i in range(n):
        for word in words:
            if i + 1 >= len(word) and target[i-len(word)+1:i+1] == word:
                dp[i] = min(dp[i], dp[i-len(word)] + 1)

    return dp[n-1] if dp[n-1] != float('inf') else -1

def minValidStrings(words, target):
    n = len(target)
    dp = [float('inf')] * n
    dp[0] = 0

    for i in range(n):
        for word in words:
            if i >= len(word) - 1 and target[i-len(word)+1:i+1] == word:
                dp[i] = min(dp[i], dp[i-len(word)] + 1 if i > len(word) - 1 else 1)

    return dp[-1] if dp[-1] != float('inf') else -1

