import heapq

def kthObstacleDistance(queries, k):
    heap = []
    results = []

    for x, y in queries:
        distance = abs(x) + abs(y)
        heapq.heappush(heap, distance)

        if len(heap) < k:
            results.append(-1)
        else:
            results.append(heap[k-1])

    return results

import heapq

def kthObstacleDistance(queries, k):
    min_heap = []
    results = []

    for x, y in queries:
        distance = abs(x) + abs(y)
        heapq.heappush(min_heap, distance)

        if len(min_heap) > k:
            heapq.heappop(min_heap)

        if len(min_heap) < k:
            results.append(-1)
        else:
            results.append(min_heap[0])

    return results

import heapq

def kth_nearest_obstacle(queries, k):
    # Min-heap to store the distances of obstacles from the origin
    heap = []

    results = []

    for x, y in queries:
        # Calculate the Manhattan distance from the origin
        distance = abs(x) + abs(y)

        # Add the distance to the heap
        if len(heap) < k:
            heapq.heappush(heap, distance)
        else:
            # Replace the largest distance with the new one if it's smaller
            if distance < heap[0]:
                heapq.heapreplace(heap, distance)

    # Determine the result based on the size of the heap
    if len(heap) < k:
        results.append(-1)
    else:
        results.append(heap[0])

    return results

