from collections import deque

def min_transformations(S, T):
    queue = deque([(S, [S])])
    visited = set([S])
    min_length = float('inf')
    best_path = None

    while queue:
        current, path = queue.popleft()
        if len(path) > min_length:
            continue

        if current == T:
            if len(path) < min_length or (len(path) == min_length and path < best_path):
                min_length = len(path)
                best_path = path
            continue

        for i in range(len(current)):
            for char in 'abcdefghijklmnopqrstuvwxyz':
                if char != current[i]:
                    next_str = current[:i] + char + current[i+1:]
                    if next_str not in visited:
                        visited.add(next_str)
                        queue.append((next_str, path + [next_str]))

    return min_length - 1, best_path

