def count_substrings(word, k):
    vowels = set('aeiou')
    n = len(word)
    count = 0

    for i in range(n):
        vowel_count = [0] * 5
        consonant_count = 0

        for j in range(i, n):
            char = word[j]
            if char in vowels:
                vowel_count[vowels.index(char)] += 1
            else:
                consonant_count += 1

            if all(v > 0 for v in vowel_count) and consonant_count == k:
                count += 1

    return count

def count_substrings(word: str, k: int) -> int:
    vowels = {'a', 'e', 'i', 'o', 'u'}
    n = len(word)
    count = 0
    left = 0
    vowel_count = {v: 0 for v in vowels}
    consonant_count = 0

    for right in range(n):
        if word[right] in vowels:
            vowel_count[word[right]] += 1
        else:
            consonant_count += 1

        while all(vowel_count[v] > 0 for v in vowels) and consonant_count == k + len(vowels):
            count += n - right
            if word[left] in vowels:
                vowel_count[word[left]] -= 1
            else:
                consonant_count -= 1
            left += 1

    return count

