def can_reach_final_cell(grid, health):
    m, n = len(grid), len(grid[0])

    def dfs(x, y, health):
        if x < 0 or x >= m or y < 0 or y >= n or health <= 0:
            return False
        if x == m - 1 and y == n - 1:
            return health >= 1
        if grid[x][y] == 1:
            health -= 1

        # Mark the cell as visited by setting it to -1
        temp = grid[x][y]
        grid[x][y] = -1

        # Explore all four directions
        if dfs(x + 1, y, health) or dfs(x - 1, y, health) or dfs(x, y + 1, health) or dfs(x, y - 1, health):
            return True

        # Backtrack
        grid[x][y] = temp
        return False

    return dfs(0, 0, health)

def hasPath(grid, health):
    m, n = len(grid), len(grid[0])

    def dfs(i, j, health):
        if i < 0 or i >= m or j < 0 or j >= n or health <= 0:
            return False
        if i == m - 1 and j == n - 1:
            return health >= 1
        grid[i][j] = -1  # Mark as visited
        result = (dfs(i - 1, j, health - grid[i][j]) or
                  dfs(i + 1, j, health - grid[i][j]) or
                  dfs(i, j - 1, health - grid[i][j]) or
                  dfs(i, j + 1, health - grid[i][j]))
        grid[i][j] = 0  # Unmark
        return result

    return dfs(0, 0, health)

