            return False
        if x == len(grid) - 1 and y == len(grid[0]) - 1:
            return current_health >= 1

        temp = grid[x][y]
        grid[x][y] = 1  # Mark the cell as visited by setting it to 1

        directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
        for dx, dy in directions:
