def maxTowerHeight(maximumHeight):
    maximumHeight.sort(reverse=True)
    unique_heights = set()
    current_height = 1

    for mh in maximumHeight:
        while current_height in unique_heights:
            current_height += 1
        if current_height > mh:
            return -1
        unique_heights.add(current_height)

    return sum(unique_heights)

