import heapq

def shortest_path(n, edges):
    graph = [[] for _ in range(n)]
    for u, v in edges:
        graph[u].append((v, 1))

    def dijkstra(start):
        dist = [float('inf')] * n
        dist[start] = 0
        pq = [(0, start)]

        while pq:
            current_dist, node = heapq.heappop(pq)
            if current_dist > dist[node]:
                continue

            for neighbor, weight in graph[node]:
                distance = current_dist + weight

                if distance < dist[neighbor]:
                    dist[neighbor] = distance
                    heapq.heappush(pq, (distance, neighbor))

        return dist[n-1]

    results = []
    for edge in edges:
        edges.append(edge)
        results.append(dijkstra(0))

    return results[:-1]

def process_queries(n, queries):
    results = []
    current_edges = []

    for u, v in queries:
        current_edges.append([u, v])
        results.append(shortest_path(n, current_edges))

    return results
