    # Position of k-th character in the concatenated string
    pos = k - ((n * (n - 1)) // 2) - 1

    # Character at the calculated position
    return chr((pos % 26) + ord('a'))

def findKthCharacter(k):
    current_char = 'a'
    length = 1

    while True:
        if k <= length:
